package Method;
//Information can be passed to methods as a parameter//
public class Parameters {
	static void mymethod(String firstname,int age)
	{
		System.out.println(firstname +"Thomas"+age);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Parameters.mymethod("Ruby",6);
		Parameters.mymethod("Prince",23);
		

	}

}
